function navClick(e){let t=document.querySelector(".nav > .left"),a=document.querySelector(".nav > .right");t.querySelector(".active").classList.remove("active"),a.querySelector(".active").classList.remove("active"),t.querySelector(`span[data-config='${e}']`).classList.add("active"),a.querySelector(`span[data-config='${e}']`).classList.add("active")}document.addEventListener("click",e=>{e.target.closest(".nav > .left")&&e.target.closest("span[data-config]:not(.active)")&&navClick(e.target.closest("span[data-config]").getAttribute("data-config"))});